INSERT INTO `wp_posts` VALUES(54, 1, '2012-04-26 21:25:22', '2012-04-26 21:25:22', '<h3>Contact the BIRN</h3>\r\n<strong>E-mail: </strong> All questions, concerns, and comments regarding broadcasts, interviews, or anything in between should be directed to <a href="mailto:info@thebirn.com">info@thebirn.com</a>\r\n<strong>Office Phone: </strong> 617.747.8538\r\n<strong>Postal Address:</strong>\r\nBIRN\r\nMS-150\r\nBerklee College of Music\r\n1140 Boylston Street\r\nBoston, MA 02215-3693\r\n<h3>Submit Your Music</h3>\r\nIf you are looking to submit your music to our station, check out the <a href="/submissions">Submissions</a> page.\r\n<h3>On-Air Studio Contacts</h3>\r\n<strong>AIM: </strong><a href="aim:addbuddy?screenname=thebirn" target="_blank">thebirn</a>\r\n<strong>BIRN Hotline: </strong>617.651.BIRN (2476)\r\n<h3>BIRN Newsletter</h3>\r\nTo receive the BIRN''s monthly newsletter, with exclusive free recordings, reviews, updates and more, sign up <a href="http://visitor.r20.constantcontact.com/d.jsp?llr=bwonnycab&amp;p=oi&amp;m=1102478490305">here</a>.\r\n<h3>Berklee Alumni</h3>\r\nYour music can be featured on our Air Alumni Channel, BIRN3! For more information, please contact <a href="mailto:airalumni@thebirn.com">airalumni@thebirn.com</a>\r\n<strong>Postal Address:</strong>\r\nAir Alumni\r\nAttn.: Adam Olenn\r\nMS-1140-IA\r\nBerklee College of Music\r\n1140 Boylston Street\r\nBoston, MA 02215\r\n<h3>BIN on the BIRN</h3>\r\nJason Camelio, Associate Director of International Programs - Audition\r\n<strong>Phone: </strong>617.747.3187\r\n<strong>E-mail: </strong><a href="mailto:jcamelio@berklee.edu">jcamelio@berklee.edu</a>\r\n<strong>Skype: </strong>jcamelio1', 'Contact', '', 'publish', 'open', 'open', '', 'contact', '', '', '2012-05-18 12:59:22', '2012-05-18 12:59:22', '', 0, 'http://localhost:8888/?page_id=54', 0, 'page', '', 0);
