INSERT INTO `wp_posts` VALUES(187, 1, '2012-05-05 12:56:15', '2012-05-05 12:56:15', '	    <h3>Submissions</h3>\r\n      <p>Thank you for your interest in the BIRN. Read the following criteria carefully as you will need this information to properly submit any material.</p>\r\n      <ul>\r\n        <li><strong>The BIRN only accepts CDs for airplay consideration.</strong> We cannot accept files attached to emails or music in any other form. We need a physical copy on a CD. Note: The BIRN will not return any CDs.</li>\r\n        <li><strong>The BIRN will only play the submitted music if a BIRN DJ chooses to.</strong> You should try to identify that DJ in your submission.</li>\r\n        <li><strong>The BIRN will not play music with obscenities or indecent content.</strong> The BIRN observes our own ''Community Standards'' and FCC regulations and guidelines.</li>\r\n        <li><strong>If you have any questions</strong> please email <a href="mailto:info@thebirn.com">info@thebirn.com</a></li>\r\n      </ul>\r\n      <p>\r\n        To submit your music, print and fill out <a href="http://media.thebirn.com/BIRN-release.pdf">The Submission Form</a>. Please include a onesheet that provides more information about the artist. The finished form and CD should be mailed in a sealed padded envelope to:<br />\r\n        <center>\r\n          <strong>\r\n            The BIRN<br />\r\n            Berklee College of Music<br />\r\n            MS-150<br />\r\n            1140 Boylston Street<br />\r\n            Boston, MA 02215\r\n          </strong>\r\n        </center>\r\n      </p>\r\n\r\n      <p>\r\n        If you are at Berklee or in Boston, you can drop off your sealed envelope at the Professional Writing Center located at 150 Massachusetts Avenue.  Or you can drop it in the Submissions Box outside our door on the third floor of the Uchida Building at 921 Boylston St.  You can also find a form there and fill it out to accompany your submission.<br />\r\n        *It is highly recommended that artist and track information be submitted to <a href="http://www.gracenote.com/">Gracenote</a> before sending to The BIRN.\r\n      </p>\r\n\r\n      <p>To download a copy of these policies, please click here: <a href="http://media.thebirn.com/BIRN_submit_policy.pdf">The BIRN Submission Policy</a></p>\r\n  \r\n    <h3>Air Alumni</h3>\r\n    <p>If you are an alumnus and would like to submit your music for play on our alumni channel, please use the following contact information.<br />\r\n    <center>\r\n      Air Alumni<br />\r\n      Attn.: Adam Olenn<br />\r\n      MS-1140-IA<br />\r\n      Berklee College of Music<br />\r\n      1140 Boylston Street<br />\r\n      Boston, MA 02215<br />\r\n      <br />\r\n      Email: <a href="mailto:airalumni@thebirn.com">airalumni@thebirn.com</a>\r\n    </center>\r\n</p>	', 'Submissions', '', 'inherit', 'open', 'open', '', '56-revision-7', '', '', '2012-05-05 12:56:15', '2012-05-05 12:56:15', '', 56, 'http://localhost:8888/56-revision-7/', 0, 'revision', '', 0);
