INSERT INTO `wp_posts` VALUES(244, 1, '2012-05-05 13:08:43', '2012-05-05 13:08:43', '<h3 style="text-align: center;">the BIRN: Musician Radio from the Berklee College of Music</h3>\r\n<h1 style="text-align: center;"><span style="color: #edf6cf;">''Powered by Players'' - <em>since 2004</em></span></h1>\r\nThe Berklee Internet Radio Network is a project of the Berklee College of Music and is designed, operated, and maintained by the Berklee community of students, faculty, and staff.\r\n<ul>\r\n	<li>We are a commercial-free, internet radio station.</li>\r\n	<li>We broadcast around the clock on six unique channels (see the lineup and descriptions on our <a href="/listen">Channels</a> page).</li>\r\n	<li>We are a Noncommercial Educational Entity (NEE). More info is provided at <a href="http://www.soundexchange.com">http://www.soundexchange.com</a></li>\r\n	<li>All of our DJs are musicians, bringing a unique perspective to both our playlists and commentary.</li>\r\n	<li>The student group involved in operating the station meets weekly. All Berklee students are welcome at General Meetings.</li>\r\n	<li>Students can also elect to take the 1-credit, MTEC-200 class, Berklee Internet Radio Workshop, held concurrently with the General Meeting.</li>\r\n</ul>\r\n<h3>Programming</h3>\r\n<ul>\r\n	<li>Our DJs have complete creative control of content.</li>\r\n	<li>We are a College Music Journal-reporting station.</li>\r\n	<li>BIRN accepts new music submissions. See the info on our <a href="/submissions">Submissions</a> page.</li>\r\n</ul>\r\n&nbsp;', 'About The BIRN', '', 'inherit', 'open', 'open', '', '20-revision-11', '', '', '2012-05-05 13:08:43', '2012-05-05 13:08:43', '', 20, 'http://localhost:8888/20-revision-11/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(245, 1, '2012-05-05 13:02:29', '2012-05-05 13:02:29', '	  <h3>Contact the BIRN</h3>\r\n\r\n<p><strong>E-mail: </strong> All questions, concerns, and comments regarding broadcasts, interviews, or anything in between should be directed to <a href="mailto:info@thebirn.com">info@thebirn.com</a><br />\r\n<strong>Office Phone: </strong> 617.747.8538<br />\r\n<strong>Postal Address:</strong><br />\r\n      BIRN<br />\r\n      MS-150<br />\r\n      Berklee College of Music<br />\r\n      1140 Boylston Street<br />\r\n      Boston, MA 02215-3693\r\n</p>\r\n\r\n<h3> Submit Your Music</h3>\r\nIf you are looking to submit your music to our station, check out the <a href="/submissions">Submissions</a> page.\r\n\r\n<h3>On-Air Studio Contacts</h3>\r\n<p><strong>AIM: </strong><a href="aim:addbuddy?screenname=thebirn" target="_blank">thebirn</a><br />\r\n<strong>BIRN Hotline: </strong>617.651.BIRN (2476)</p>\r\n\r\n<h3>BIRN Newsletter</h3>\r\n<p>To receive the BIRN''s monthly newsletter, with exclusive free recordings, reviews, updates and more, sign up <a href="http://visitor.r20.constantcontact.com/d.jsp?llr=bwonnycab&p=oi&m=1102478490305">here</a>.\r\n</p>\r\n\r\n<h3>Berklee Alumni</h3>\r\n<p>Your music can be featured on our Air Alumni Channel, BIRN3! For more information, please contact <a href="mailto:airalumni@thebirn.com">airalumni@thebirn.com</a><br />\r\n<strong>Postal Address:</strong><br />\r\n      Air Alumni<br />\r\n      Attn.: Adam Olenn<br />\r\n      MS-1140-IA<br />\r\n      Berklee College of Music<br />\r\n      1140 Boylston Street<br />\r\n      Boston, MA 02215\r\n</p>\r\n\r\n<h3>BIN on the BIRN</h3>\r\n<p>Jason Camelio, Associate Director of International Programs - Audition<br />\r\n<strong>Phone: </strong>617.747.3187<br />\r\n<strong>E-mail: </strong><a href="mailto:jcamelio@berklee.edu">jcamelio@berklee.edu</a><br />\r\n<strong>Skype: </strong>jcamelio1</p>	\r\n', 'Contact', '', 'inherit', 'open', 'open', '', '54-revision-6', '', '', '2012-05-05 13:02:29', '2012-05-05 13:02:29', '', 54, 'http://localhost:8888/54-revision-6/', 0, 'revision', '', 0);
